# get_fits ----
## Setup ----
# Load or prepare any necessary data for testing
# Borrowed functionality from test-get_estimates.R, in case this is called first
if (!file.exists(test_path("fixtures", "fit_age_length_comp.RDS"))) {
  prepare_test_data()
}

## IO correctness ----
# Define the expected column names for the fits tibble
expected_colnames <- c(
  "module_name", "module_id", "label", "data_id", "fleet_name",
  "unit", "uncertainty", "age", "length", "datestart",
  "dateend", "year", "init", "expected", "log_like",
  "distribution", "re_estimated", "log_like_cv", "weight"
)

test_that("get_fits() works with deterministic run", {
  # Read the RDS file containing the deterministic run results
  deterministic_results <- readRDS(test_path("fixtures", "deterministic_age_length_comp.RDS"))
  deterministic_colnames <- get_fits(deterministic_results) |> colnames()
  #' @description Test that [get_fits()] returns correct colnames from a
  #' deterministic run.
  expect_equal(
    object = deterministic_colnames,
    expected = expected_colnames
  )

  #' @description Test that [get_fits()] returns correct snapshot from a
  #' deterministic run.
  expect_snapshot(
    get_fits(deterministic_results) |>
      # Remove the expected and log_like columns, as they
      # may change between runs
      dplyr::select(-expected, -log_like) |>
      print(n = 320, width = Inf)
  )
})

test_that("get_fits() works with estimation run", {
  # Load the test data from an RDS file containing model fits.
  # List all RDS files in the fixtures directory that match the pattern "fit*_.RDS"
  fit_files <- list.files(
    path = test_path("fixtures"),
    pattern = "^fit.*\\.RDS$",
    full.names = TRUE
  )

  # Function to read the RDS file, get fits, and check column names
  check_fits_colnames <- function(fit_file) {
    fit_data <- readRDS(fit_file)
    fits <- get_fits(fit_data)
    fits_colnames <- colnames(fits)
    #' @description Test that [get_fits()] returns correct output for the
    #' fits slot.
    expect_equal(
      object = fits,
      expected = fit_data@fits
    )
    #' @description Test that [get_fits()] returns correct column names
    #' for the fits tibble.
    expect_equal(
      object = fits_colnames,
      expected = expected_colnames
    )
  }

  # Use purrr::map to apply the function to each file
  result <- purrr::map(fit_files, check_fits_colnames)

  #' @description Test that [get_fits()] returns correct snapshot for an
  #' estimation run.
  expect_snapshot(
    # Read the first RDS file, get estimates, and print a snapshot
    readRDS(fit_files[[1]]) |>
      get_fits() |>
      # Remove the expected and log_like columns, as they
      # may change between runs
      dplyr::select(-expected, -log_like) |>
      print(n = 320, width = Inf)
  )
})

## Edge handling ----
test_that("get_fits() returns correct outputs for edge cases", {
  #' @description Test that [get_fits()] returns an error when given
  #' invalid arguments.
  expect_error(
    object = get_fits("invalid_fit")
  )
})

