# rcpp population interface ----
## Setup ----
# Load or prepare any necessary data for testing

## IO correctness ----
test_that("rcpp population interface works with correct inputs", {
  # setup population to create test values
  population <- methods::new(Population)
  n_years <- 10
  n_ages <- 10
  log_M_length <- n_years * n_ages
  population$log_M$resize(log_M_length)
  purrr::walk(
    seq_along(1:log_M_length),
    \(x) population$log_M[x]$value <- -1
  )
  population$log_init_naa$resize(n_ages)
  purrr::walk(
    seq_along(1:n_ages),
    \(x) population$log_init_naa[x]$value <- 0
  )
  population$log_init_naa$set_all_estimable(TRUE)
  population$n_ages$set(n_ages)
  population$ages$resize(n_ages)
  purrr::walk(
    seq_along(1:n_ages),
    \(x) population$ages$set(x - 1, x)
  )
  population$n_fleets$set(2)
  population$n_years$set(n_years)


  #' @description Test that the population id is 1.
  expect_equal(
    object = population$get_id(),
    expected = 1
  )

  for (i in 1:(n_years * n_ages)) {
    #' @description Test that the log_M values are all -1.
    expect_equal(
      object = population$log_M[i]$value,
      expected = -1
    )
    #' @description Test that the log_M values are not estimated.
    expect_equal(
      object = population$log_M[i]$estimation_type$get(), "constant"
    )
  }

  for (i in 1:n_years) {
    #' @description Test that the log_init_naa values are all 0.
    expect_equal(
      object = population$log_init_naa[i]$value,
      expected = 0
    )
    #' @description Test that the log_init_naa values are estimated.
    expect_equal(
      object = population$log_init_naa[i]$estimation_type$get(), "fixed_effects"
    )
  }

  population$log_init_naa$set_all_estimable(FALSE)
  population$log_M$set_all_estimable(TRUE)

  for (i in 1:(n_years * n_ages)) {
    #' @description Test that the log_M values are all -1.
    expect_equal(
      object = population$log_M[i]$value,
      expected = -1
    )
    #' @description Test that the log_M values are now estimated.
    expect_equal(
      object = population$log_M[i]$estimation_type$get(), "fixed_effects"
    )
  }

  for (i in 1:n_years) {
    #' @description Test that the log_init_naa values are all 0.
    expect_equal(
      object = population$log_init_naa[i]$value,
      expected = 0
    )
    #' @description Test that the log_init_naa values not estimated.
    expect_equal(
      object = population$log_init_naa[i]$estimation_type$get(), "constant"
    )
  }

  clear()
})

